<script>
import { ref } from '@vue/reactivity';
import axios from 'axios';
import { createToaster } from "@meforma/vue-toaster";
import HeaderView from '../components/HeaderView.vue';
import SideBarView from '../components/SideBarView.vue';
import FooterView from '../components/FooterView.vue';
import NavView from '../components/NavView.vue';
import ChatBox from '../components/ChatBox.vue';
import Wallet from '../components/Wallet.vue';
export default {
    name: "home",
    components: {
        NavView,
        ChatBox,
        HeaderView,
        SideBarView,
        Wallet,
        FooterView
    },
    setup() {

        const students = ref([]);
        const departments = ref([]);
        const schools = ref([]);
        const achats = ref([]);
        const consommations = ref([]);
        const user = ref([]);
        const user_category = localStorage.getItem("user_category");
        const username = localStorage.getItem("username");
        const stock_essence = ref(0);
        const stock_mazout = ref(0);

		axios.defaults.headers.common.Authorization = `Bearer ${localStorage.getItem('token')}`;

        const toaster = createToaster({
            position: "top-right",
            pauseOnHover: "true",
            dismissible: "true"
        });

        fetch_students()
        fetch_departments()
        getSchools()
        fetch_achats()
        fetch_station_profil();
        fetch_consommations();
        fetch_essence_status();
        fetch_station_profil();

        function fetch_students() {
            //Axios request
			axios
				.get('http://localhost:8080/students').then(response => {
					if (response) {
						if (response.data.httpStatus == "OK") {
							toaster.show(`students fetched`, {
								type: "success"
							});
							students.value = response.data.data
						} else {
							toaster.show(response.data.message, {
								type: "warning"
							});
						}
					}
				})
				.catch(error => {
					toaster.error(`Failed to fetch students... - ${error}`, {
						type: "error",
					});
				})
				.finally(() => null)   
        }

        function fetch_departments() {
            //Axios request
			axios
				.get('http://localhost:8080/departments').then(response => {
					if (response) {
						if (response.data.httpStatus == "OK") {
							toaster.show(`departments fetched`, {
								type: "success"
							});
							departments.value = response.data.data
						} else {
							toaster.show(response.data.message, {
								type: "warning"
							});
						}
					}
				})
				.catch(error => {
					toaster.error(`Failed to fetch departments... - ${error}`, {
						type: "error",
					});
				})
				.finally(() => null)
        }

        function getSchools() {
            //Axios request
			axios
				.get('http://localhost:8080/schools').then(response => {
					if (response) {
						if (response.data.httpStatus == "OK") {
							schools.value = response.data.data
						} else {
							toaster.show(response.data.message, {
								type: "warning"
							});
						}
					}
				})
				.catch(error => {
					toaster.error(`Failed to fetch schools... - ${error}`, {
						type: "error",
					});
				})
				.finally(() => null)
        }

        function fetch_achats() {
            achats.value = 0
            //Axios request
        }

        function fetch_consommations() {
            //Axios request
            consommations.value = 0
            //Axios request
        }

        function fetch_essence_status(id) {
            //Axios request
            stock_essence.value = 0;
            //Axios request
        }

        function fetch_mazout_status(id) {
            //Axios request
            stock_mazout.value = 0;
            //Axios request
        }


        function fetch_station_profil() {
            //Axios request
            
            //Axios request
        }

        return {
            students,
			schools,
            departments,
            consommations,
            user_category,
            username,
            user
        }
    },
}
